mapper.select:
		select `id`,`user_id`,`open_id`,`nickname`,`password`,`phone`,sign_name,`heard_url`,`name`,`register_time`,`balance`,`login_time`,`update_time`,`delete_flag`,`username`,`sex`,`birthday`,login_user_flag from t_user

		  		   and id = #{id} 
		  		   and user_id = #{userId} 
		  		   and open_id = #{openId} 
		  		   and nickname = #{nickname} 
		  		   and password = #{password} 
		  		   and phone = #{phone} 
		  		   and heard_url = #{heardUrl} 
		  		   and name = #{name} 
		  		   and register_time = #{registerTime} 
		  		   and balance = #{balance} 
		  		   and login_time = #{loginTime} 
		  		   and update_time = #{updateTime} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and username = #{username} 
		  		   and sex = #{sex} 
		  		   and birthday = #{birthday} 
		  		   and login_user_flag = #{loginUserFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

